
public class Stringexample6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s = "Indus university";
		//charAt//
		char ch=s.charAt(7);
		System.out.println(ch);
		//contains//
		System.out.println(s.contains("Indus"));
		System.out.println(s.contains("indus"));
		

	}

}
